import React, { useEffect, useState } from "react";
import {
    Chart as ChartJS,
    LineElement,
    PointElement,
    CategoryScale,
    LinearScale,
    Legend,
    Tooltip,
    Title,
} from "chart.js";
import { Line } from "react-chartjs-2";
import Axios from "axios";

ChartJS.register(Legend, Tooltip, Title, CategoryScale, LinearScale, LineElement, PointElement);

const LineChart = (timestamp) => {
    var [dhtList, setDhtList] = useState([]);
    // console.log(timestamp);

    useEffect(() => {
        Axios.get("http://localhost:8000/dhts").then((response) => {
            setDhtList(response.data);
            console.log(response.data);
        });
    }, []);

    var data = {
        labels: dhtList.map((item) => {
            return (
                new Date(item.created_at).getHours() +
                ":" +
                new Date(item.created_at).getMinutes() +
                ":" +
                new Date(item.created_at).getSeconds()
            );
        }),
        datasets: [
            {
                label: "Temperature (°C)",
                data: dhtList.map((item) => {
                    return item.temperature;
                }),
                backgroundColor: "transparent",
                borderColor: "red",
                borderWidth: 4,
            },
            {
                label: "Humidity (%)",
                data: dhtList.map((item) => {
                    return item.humidity;
                }),
                backgroundColor: "transparent",
                borderColor: "green",
                borderWidth: 4,
            },
        ],
    };

    const options = {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
            y: {
                beginAtZero: true,
            },
        },
        legend: {
            labels: {
                fontSize: 26,
            },
        },
        plugins: {
            legend: {
                position: "top",
            },
            title: {
                display: true,
                text: "Chart.js Bar Chart",
            },
        },
        title: {
            display: true,
            text: "Temperature And Humidity Chart",
        },
    };

    return (
        <div>
            <Line data={data} height={400} width={800} options={options} />
        </div>
    );
};

export default LineChart;
