import React, { useEffect, useState } from "react";
import {
    Chart as ChartJS,
    LineElement,
    PointElement,
    CategoryScale,
    LinearScale,
    Legend,
    Tooltip,
    Title,
} from "chart.js";
import { Line } from "react-chartjs-2";
import Axios from "axios";
import "./LineChart.css";

ChartJS.register(Legend, Tooltip, Title, CategoryScale, LinearScale, LineElement, PointElement);

const LineChart = (option) => {
    var [dhtList, setDhtList] = useState([]);
    var labelsTemp;
    // console.log(option);

    useEffect(() => {
        Axios.get(`http://localhost:8000/dhts/${option.option}`).then((response) => {
            setDhtList(response.data);
        });
    }, [option]);

    const getLabels = () => {
        if (option.option === "current") {
            labelsTemp = dhtList.map((item) => {
                return (
                    new Date(item.created_at).getHours() +
                    ":" +
                    new Date(item.created_at).getMinutes() +
                    ":" +
                    new Date(item.created_at).getSeconds()
                );
            });
        }
        if (option.option === "latest_3_days") {
            labelsTemp = dhtList.map((item) => {
                return new Date(item.Days).getDay();
            });
        }
        if (option.option === "week") {
            labelsTemp = dhtList.map((item) => {
                return item.Days;
            });
        }
        if (option.option === "month") {
            labelsTemp = dhtList.map((item) => {
                return item.Days;
            });
        }
        console.log(labelsTemp);
        return labelsTemp;
    };

    var data = {
        labels: getLabels(),
        datasets: [
            {
                label: "Temperature (°C)",
                data: dhtList.map((item) => {
                    return item.temperature;
                }),
                backgroundColor: "transparent",
                borderColor: "red",
                borderWidth: 4,
            },
            {
                label: "Humidity (%)",
                data: dhtList.map((item) => {
                    return item.humidity;
                }),
                backgroundColor: "transparent",
                borderColor: "green",
                borderWidth: 4,
            },
        ],
    };

    var options = {
        // responsive: true,
        maintainAspectRatio: false,
        scales: {
            y: {
                beginAtZero: true,
            },
            xAxisID: {
                title: {
                    display: true,
                    text: "Time",
                    color: "#000",
                    font: {
                        size: 20,
                    },
                },
                ticks: {
                    display: true,
                },
            },
            yAxisID: {
                title: {
                    display: true,
                    text: "Value",
                    color: "#000",
                    font: {
                        size: 20,
                    },
                },
                ticks: {
                    display: false,
                },
                grid: {
                    display: false,
                },
            },
        },
        plugins: {
            legend: {
                position: "top",
            },
            title: {
                display: true,
                text: "Temperature And Humidity Chart",
                color: "#000",
                font: {
                    size: 24,
                },
            },
        },
    };

    return (
        <div>
            <article className="canvas-container">
                <Line data={data} options={options} />
            </article>
        </div>
    );
};

export default LineChart;
