import React from "react";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import LineChart from "../components/LineChart";
import { io } from "socket.io-client";
import "./Control.css";
import { Link } from "react-router-dom";

const socket = io("localhost:8000");

const lamp_on = "/images/lamp_on.png";
const lamp_off = "/images/lamp_off.png";

const Control = () => {
    const [status1, setStatus1] = useState("off");
    const [status2, setStatus2] = useState("off");
    const [status3, setStatus3] = useState("off");
    const [status4, setStatus4] = useState("off");
    const [option, setOption] = useState("current");

    useEffect(() => {
        socket.on("data_dht", (data) => {
            let temperatureElement = document.getElementById("temperature");
            let humidityElement = document.getElementById("humidity");
            temperatureElement.value = data.dht[0];
            humidityElement.value = data.dht[1];
        });

        socket.on("data_devices", (data) => {
            if (data.device_1 === "A1_ON") {
                setStatus1("on");
            } else if (data.device_1 === "A1_OFF") {
                setStatus1("off");
            }

            if (data.device_2 === "A2_ON") {
                setStatus2("on");
            } else if (data.device_2 === "A2_OFF") {
                setStatus2("off");
            }
        });
    }, [socket]);

    const changeStatus1 = () => {
        if (status1 === "off") {
            setStatus1("on");
            socket.emit("status_device", "A1_ON");
        } else {
            setStatus1("off");
            socket.emit("status_device", "A1_OFF");
        }
    };

    const changeStatus2 = () => {
        if (status2 === "off") {
            setStatus2("on");
            socket.emit("status_device", "A2_ON");
        } else {
            setStatus2("off");
            socket.emit("status_device", "A2_OFF");
        }
    };

    const changeStatus3 = () => {
        if (status3 === "off") {
            setStatus3("on");
            // socket.emit("status_device", "A3_ON");
        } else {
            setStatus3("off");
            // socket.emit("status_device", "A3_OFF");
        }
    };

    const changeStatus4 = () => {
        if (status4 === "off") {
            setStatus4("on");
            // socket.emit("status_device", "A4_ON");
        } else {
            setStatus4("off");
            // socket.emit("status_device", "A4_OFF");
        }
    };

    function chartTracker(value) {
        setOption(value);
    }

    return (
        <>
            <div className="temperature-humidity">
                <label>Temperature: </label>
                <input id="temperature" type="text" />
                <label>Humidity: </label>
                <input id="humidity" type="text" />
            </div>

            <div className="container">
                <div className="row justify-content-between">
                    <div className="col">
                        <div className="device-list">
                            <span>Device 1</span>
                            <img src={status1 === "off" ? lamp_off : lamp_on} alt="Device 1" />
                            <button
                                id="device1"
                                style={{ backgroundColor: status1 === "off" ? "red" : "green" }}
                                onClick={changeStatus1}
                            >
                                {status1 === "off" ? "Off" : "On"}
                            </button>
                        </div>
                    </div>
                    <div className="col">
                        <div className="device-list">
                            <span>Device 2</span>
                            <img src={status2 === "off" ? lamp_off : lamp_on} alt="Device 2" />
                            <button
                                id="device2"
                                style={{ backgroundColor: status2 === "off" ? "red" : "green" }}
                                onClick={changeStatus2}
                            >
                                {status2 === "off" ? "Off" : "On"}
                            </button>
                        </div>
                    </div>
                </div>
                <div className="row justify-content-between">
                    <div className="col">
                        <div className="device-list">
                            <span>Device 3</span>
                            <img src={status3 === "off" ? lamp_off : lamp_on} alt="Device 3" />
                            <button
                                id="device3"
                                style={{ backgroundColor: status3 === "off" ? "red" : "green" }}
                                onClick={changeStatus3}
                            >
                                {status3 === "off" ? "Off" : "On"}
                            </button>
                        </div>
                    </div>
                    <div className="col">
                        <div className="device-list">
                            <span>Device 4</span>
                            <img src={status4 === "off" ? lamp_off : lamp_on} alt="Device 4" />
                            <button
                                id="device4"
                                style={{ backgroundColor: status4 === "off" ? "red" : "green" }}
                                onClick={changeStatus4}
                            >
                                {status4 === "off" ? "Off" : "On"}
                            </button>
                        </div>
                    </div>
                </div>
            </div>

            <div className="container-vis">
                <LineChart option={option} />
                <div className="selectBox">
                    <select onChange={(e) => chartTracker(e.target.value)}>
                        <option value="current">Current</option>
                        <option value="latest_3_days">Latest 3 Days</option>
                        <option value="week">Week</option>
                        <option value="month">Month</option>
                    </select>
                </div>
            </div>
        </>
    );
};

export default Control;
