import React, { useEffect, useState } from "react";
import {
    Chart as ChartJS,
    LineElement,
    PointElement,
    CategoryScale,
    LinearScale,
    Legend,
    Tooltip,
    Title,
} from "chart.js";
import { Line } from "react-chartjs-2";
import Axios from "axios";
import "./LineChart.css";

ChartJS.register(Legend, Tooltip, Title, CategoryScale, LinearScale, LineElement, PointElement);

const LineChart = (option) => {
    var [dhtList, setDhtList] = useState([]);
    var [labelsTemp, setLabelsTemp] = useState([]);
    // console.log(option);

    useEffect(() => {
        Axios.get(`http://localhost:8000/dhts/${option.option}`).then((response) => {
            setDhtList(response.data);
            // console.log(option.option);
        });
        if (dhtList.length > 0) {
            if (option.option === "current") {
                setLabelsTemp(
                    dhtList.map((item) => {
                        return (
                            new Date(item.created_at).getHours() +
                            ":" +
                            new Date(item.created_at).getMinutes() +
                            ":" +
                            new Date(item.created_at).getSeconds()
                        );
                    })
                );
            } else if (option.option === "latest_3_days") {
                setLabelsTemp(
                    dhtList.map((item) => {
                        return item.Days;
                    })
                );
            } else if (option.option === "week") {
                setLabelsTemp(
                    dhtList.map((item) => {
                        return item.Days;
                    })
                );
            } else if (option.option === "month") {
                setLabelsTemp(
                    dhtList.map((item) => {
                        return item.Days;
                    })
                );
            }
        }
        console.log("DHT List: ", dhtList);
        console.log(labelsTemp);
    });

    var data = {
        labels: labelsTemp,
        datasets: [
            {
                label: "Temperature (°C)",
                data: dhtList.map((item) => {
                    return item.temperature;
                }),
                backgroundColor: "transparent",
                borderColor: "red",
                borderWidth: 4,
            },
            {
                label: "Humidity (%)",
                data: dhtList.map((item) => {
                    return item.humidity;
                }),
                backgroundColor: "transparent",
                borderColor: "green",
                borderWidth: 4,
            },
        ],
    };

    var options = {
        // responsive: true,
        maintainAspectRatio: false,
        scales: {
            y: {
                beginAtZero: true,
            },
            xAxisID: {
                // display: false,
                title: {
                    display: true,
                    text: "Time",
                    color: "#000",
                    font: {
                        size: 20,
                    },
                },
                ticks: {
                    display: true,
                },
            },
            yAxisID: {
                title: {
                    display: true,
                    text: "Value",
                    color: "#000",
                    font: {
                        size: 20,
                    },
                },
                ticks: {
                    display: false,
                },
                grid: {
                    display: false,
                },
            },
        },
        plugins: {
            legend: {
                position: "top",
            },
            title: {
                display: true,
                text: "Temperature And Humidity Chart",
                color: "#000",
                font: {
                    size: 24,
                },
            },
        },
    };

    return (
        <div>
            <article className="canvas-container">
                <Line data={data} options={options} />
            </article>
        </div>
    );
};

export default LineChart;
