import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import lamp_on from "./images/lamp_on.png";
import lamp_off from "./images/lamp_off.png";
import { useState } from "react";

function App() {
    const [status, setStatus] = useState("");

    const changeStatus = () => {
        status === "off" ? setStatus("On") : setStatus("Off");
    };

    return (
        <div className="main">
            <div className="container">
                <div>Display Temperature And Humidity</div>
                <div className="row">
                    <label>Temperature</label>
                    <input type="text" />
                </div>
                <div className="row">
                    <label>Humidity</label>
                    <input type="text" />
                </div>
            </div>

            <div className="container">
                <div className="row justify-content-between">
                    <div className="col-auto">
                        <div className="device-list">
                            <span>Device 1</span>
                            <img src={lamp_off} alt="Device 1" />
                            <button onClick={changeStatus}>{status === "off" ? "Off" : "On"}</button>
                        </div>
                    </div>
                    <div className="col-auto">
                        <div className="device-list">
                            <span>Device 2</span>
                            <img src={lamp_off} alt="Device 2" />
                            <button onClick={changeStatus}>Turn Off</button>
                        </div>
                    </div>
                </div>
            </div>

            <div className="container">
                <div>Display Temperature And Humidity Chart</div>
            </div>
        </div>
    );
}

export default App;
