import { useEffect, useState } from "react";
import { Routes, Route } from "react-router-dom";
import { io } from "socket.io-client";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import LineChart from "./LineChart";
import Login from "./Pages/Login";
import Home from "./Pages/Home";
import Navbar from "./components/Navbar";

const socket = io("localhost:8000");

const lamp_on = "/client/public/images/lamp_on.png";
const lamp_off = "/client/public/images/lamp_off.png";
const logo = "/client/public/images/logo.png";

function App() {
    const [status1, setStatus1] = useState("off");
    const [status2, setStatus2] = useState("off");
    const [status3, setStatus3] = useState("off");
    const [status4, setStatus4] = useState("off");
    const [option, setOption] = useState("current");

    useEffect(() => {
        socket.on("data_dht", (data) => {
            let temperatureElement = document.getElementById("temperature");
            let humidityElement = document.getElementById("humidity");
            temperatureElement.value = data.dht[0];
            humidityElement.value = data.dht[1];
        });

        socket.on("data_devices", (data) => {
            if (data.device_1 === "A1_ON") {
                setStatus1("on");
            } else if (data.device_1 === "A1_OFF") {
                setStatus1("off");
            }

            if (data.device_2 === "A2_ON") {
                setStatus2("on");
            } else if (data.device_2 === "A2_OFF") {
                setStatus2("off");
            }
        });
    }, [socket]);

    const changeStatus1 = () => {
        if (status1 === "off") {
            setStatus1("on");
            socket.emit("status_device", "A1_ON");
        } else {
            setStatus1("off");
            socket.emit("status_device", "A1_OFF");
        }
    };

    const changeStatus2 = () => {
        if (status2 === "off") {
            setStatus2("on");
            socket.emit("status_device", "A2_ON");
        } else {
            setStatus2("off");
            socket.emit("status_device", "A2_OFF");
        }
    };

    const changeStatus3 = () => {
        if (status3 === "off") {
            setStatus3("on");
            // socket.emit("status_device", "A3_ON");
        } else {
            setStatus3("off");
            // socket.emit("status_device", "A3_OFF");
        }
    };

    const changeStatus4 = () => {
        if (status4 === "off") {
            setStatus4("on");
            // socket.emit("status_device", "A4_ON");
        } else {
            setStatus4("off");
            // socket.emit("status_device", "A4_OFF");
        }
    };

    function chartTracker(value) {
        setOption(value);
    }

    return (
        <>
            <Navbar />
            <Routes>
                <Route>
                    <Route path="/" exact />
                    <Route path="/home" element={<Home />} />
                    <Route path="/login" element={<Login />} />
                </Route>
            </Routes>
        </>
    );
}

export default App;
